mapper.select:
select notice_date, shipment_company, executor, customer_short_name, sales_name, ameba_header, ameba_department, sum(rebate_amount) as rebate_amount, status, execute_style, zr_execute_date, dl_execute_date, remark, zr_file_id, dl_file_id from t_business_rebate_item where active = 1
and executor = ( select cust_name from t_customer_info where id = #{dealerId,jdbcType=INTEGER} )
#{item.custName,jdbcType=VARCHAR}
and customer_short_name like concat('%',#{customerName,jdbcType=VARCHAR},'%')
and status = #{status,jdbcType=INTEGER}
and executor = #{executor,jdbcType=VARCHAR}
and execute_style = #{executeStyle,jdbcType=VARCHAR}
and notice_date >= str_to_date(#{noticeBeginDate,jdbcType=VARCHAR}, '%Y-%m-%d')
and notice_date <= str_to_date(#{noticeEndDate,jdbcType=VARCHAR}, '%Y-%m-%d')
group by notice_date, shipment_company, executor, customer_short_name, sales_name, ameba_header, ameba_department, status, execute_style, zr_execute_date, dl_execute_date, remark, zr_file_id, dl_file_id order by notice_date desc

select

from t_business_rebate_item
where active = 1
and date_format(notice_date, '%Y-%m-%d %H:%i:%s') = date_format(#{noticeDate,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')

  and shipment_company = #{shipmentCompany,jdbcType=VARCHAR}


  and executor = #{executor,jdbcType=VARCHAR}


  and customer_short_name = #{customerShortName,jdbcType=VARCHAR}


  and sales_name = #{salesName,jdbcType=VARCHAR}


  and ameba_header = #{amebaHeader,jdbcType=VARCHAR}


  and ameba_department = #{amebaDepartment,jdbcType=VARCHAR}
