mapper.select:
select
cust.id,
cust_type,
case cust.business_type when 'A01' then 'Account Market（直供）'
when 'A02' then 'Account Market（非直供）'
when 'A03' then 'Mass Market'
when 'A04' then '代理商'
else '未知' end business_type,
cust.approve_status,
out_code,
cust.is_license,
cust_name,
cust_en_name,
cust_abbreviation,
case cust_role when 'Z001' then '中国客户(Chinese Account)'
when 'Z002' then '亚太客户(Asia-Pacific Account)'
when 'Z003' then '北美客户(North American Account)'
when 'Z010' then '内部客户(Customer)'
else '未知' end cust_role,
cust.approve_user,
cust.approve_remark,
cust.approve_time,
cust.create_time
from t_customer_info cust
where cust.business_type!='A04'
and cust.active = 1

  and cust.cust_name like concat('%',#{customerName,jdbcType=VARCHAR},'%')


  and cust_abbreviation like concat('%',#{custAbbreviation,jdbcType=VARCHAR},'%')


  and cust.out_code = #{customerOutCode,jdbcType=VARCHAR}


  and cust.is_license = #{isLicense,jdbcType=INTEGER}


  and cust.business_type = #{businessType,jdbcType=VARCHAR}


  and cust_role = #{custRole,jdbcType=VARCHAR}


  and date_format( cust.approve_time, '%Y-%m-%d' ) BETWEEN  #{reportStartDate,jdbcType=VARCHAR} and #{reportEndDate,jdbcType=VARCHAR}


  and date_format( cust.approve_time, '%Y-%m-%d' ) >=  #{reportStartDate,jdbcType=VARCHAR}


  and date_format( cust.approve_time, '%Y-%m-%d' ) <= #{reportEndDate,jdbcType=VARCHAR}


  and date_format( cust.create_time, '%Y-%m-%d' ) BETWEEN #{createStartDate,jdbcType=VARCHAR} and #{createEndDate,jdbcType=VARCHAR}


  and date_format( cust.create_time, '%Y-%m-%d' )>= #{createStartDate,jdbcType=VARCHAR}


  and date_format( cust.create_time, '%Y-%m-%d' ) <= #{createEndDate,jdbcType=VARCHAR}



    and cust.id in (
      select cust_id from t_cust_corporate_relationship where corporate_type = 'Z004' and active=1 and corporate_id = #{reportDealer,jdbcType=INTEGER}
    )


    and cust.id in (
      select cust_id from cust_zr_account_team team where active=1 and role_type = '142' and employee_id = #{reportSales,jdbcType=INTEGER}
    )


    and cust.approve_status = #{customerStatus,jdbcType=INTEGER}




    and cust.approve_status = #{customerStatus,jdbcType=INTEGER}



  and cust.approve_status != 4

    and cust.cust_type = #{custType,jdbcType=INTEGER}


    and cust.id in (
      select cust_id from cust_zr_account_team team where active=1 and role_type = '142'

      #{item}

    )


    and cust.id in (
    select cust_id from t_cust_corporate_relationship where active=1 and corporate_id = #{reportDealer,jdbcType=INTEGER}
    )


order by cust.id

select * from t_customer_info cust
where cust.active=1 and cust.business_type in ('A02','A03')
and cust.cust_name = #{custName,jdbcType=VARCHAR}

select * from t_customer_info cust
inner join t_user u on cust.id = u.dealer_id
where u.id=#{userId,jdbcType=INTEGER}

select * from t_customer_info cust
 where id in (
 select corporate_id from t_cust_corporate_relationship where corporate_type = 'Z003' and cust_id=#{custId,jdbcType=INTEGER}
 )
