select a.*,b.*,date_format(a.create_time, '%Y-%m-%d %H:%i:%s') create_time_str
from t_forecast a
inner join t_forecast_line b on a.id = b.f_id
where a.create_user_id = #{userId,jdbcType=INTEGER}


    and a.status is not null and a.status = #{status,jdbcType=INTEGER}


    and a.status != -1 and a.status is not null


    and a.status = #{status,jdbcType=INTEGER}



  and a.customer_abbreviation like concat('%', #{customerAbbreviation,jdbcType=VARCHAR}, '%')


  and a.sale_people like concat('%', #{salePeople,jdbcType=VARCHAR}, '%')


  and a.amb_leader like concat('%', #{amb,jdbcType=VARCHAR}, '%')


  and a.sd_people like concat('%', #{sd,jdbcType=VARCHAR}, '%')


