mapper.delete:
delete from t_forecast where bi_id is not null
#{item,jdbcType=INTEGER}
and 1 = 2

mapper.select:
select a.*,b.*,date_format(a.create_time, '%Y-%m-%d %H:%i:%s') create_time_str
from t_forecast a
inner join t_forecast_line b on a.id = b.f_id
where a.operation_year_month = #{yearMonth,jdbcType=VARCHAR}



      #{item,jdbcType=INTEGER}



    and 1 = 2

select batch_no from t_forecast a where 1 = 1
#{item,jdbcType=INTEGER}
group by batch_no

select count(1) from ( select a.operation_year_month,a.create_user_id from t_forecast a where 1 = 1
#{item,jdbcType=INTEGER}
group by a.operation_year_month,a.create_user_id ) t

select a.operation_year_month from t_forecast a where 1 = 1
#{item,jdbcType=INTEGER}
group by a.operation_year_month

select count(1) from t_forecast a where 1 = 1 and a.status in (1,3,4)
#{item,jdbcType=INTEGER}
and a.operation_year_month = #{operationYearMonth,jdbcType=VARCHAR}

select count(1) from t_forecast a where 1 = 1 and a.status = 1
#{item,jdbcType=INTEGER}
and a.operation_year_month = #{operationYearMonth,jdbcType=VARCHAR}

select count(1) from t_forecast a inner join t_forecast_line b on a.id = b.f_id where 1 = 1
#{item,jdbcType=INTEGER}
and (b.amb_adjustment_one is null or b.amb_adjustment_two is null or b.amb_adjustment_three is null or b.amb_adjustment_four is null or b.amb_adjustment_five is null or b.amb_adjustment_six is null)

select a.agency_status_type from t_forecast a where 1 = 1
#{item,jdbcType=INTEGER}
group by a.agency_status_type

select count(1) from t_forecast
where operation_year_month = #{operationYearMonth,jdbcType=VARCHAR}
and company = #{company,jdbcType=VARCHAR}
and customer_abbreviation = #{customerAbbreviation,jdbcType=VARCHAR}
and forecast_type = #{forecastType,jdbcType=VARCHAR}
and platform = #{platform,jdbcType=VARCHAR}
and product_model = #{productModel,jdbcType=VARCHAR}
and status in (1,2,3,4)

select a.*,b.* from t_forecast a
inner join t_forecast_line b on a.id = b.f_id
where a.operation_year_month = #{lastMonth,jdbcType=VARCHAR}
and a.company = #{company,jdbcType=VARCHAR}
and a.customer_abbreviation = #{customerAbbreviation,jdbcType=VARCHAR}
and a.forecast_type = #{forecastType,jdbcType=VARCHAR}
and a.platform = #{platform,jdbcType=VARCHAR}
and a.product_model = #{productModel,jdbcType=VARCHAR}
and a.status is not null and a.status != -1
and a.create_user_id = #{createUserId,jdbcType=INTEGER}
limit 1

mapper.update:
update t_forecast t inner join ( select a.create_user_id,a.operation_year_month from t_forecast a where 1 = 1
#{item,jdbcType=INTEGER}
group by a.create_user_id,a.operation_year_month) d on t.create_user_id = d.create_user_id and t.operation_year_month = d.operation_year_month set t.status = -1,t.operation_remark = #{operationRemark,jdbcType=VARCHAR} where t.status = 1
