mapper.select:
select d.dealer_name,t.* from t_deliver_detail t
inner join t_deliver_receive_record d on t.record_id = d.id
where t.record_id = #{recordId}

select b.* from t_deliver_receive_record a
inner join t_deliver_detail b on a.id = b.record_id
where a.dealer_id = #{dealerId}

  and a.upload_time between #{uploadStartTime} and #{uploadEndTime}


  and a.upload_time >= #{uploadStartTime}


   and a.upload_time <= #{uploadEndTime} 



  and b.delivery_date between #{handoverStartTime} and #{handoverEndTime}


  and b.delivery_date >= #{handoverStartTime}


   and b.delivery_date <= #{handoverEndTime} 


  and b.customer_full_name like concat('%', #{customerFullName}, '%')


  and b.product_model like concat('%', #{productModel}, '%')


  and b.delivery_type like concat('%', #{deliveryType}, '%')


  and b.order_month like concat('%', #{orderMonth}, '%')


  and b.customer_order_number like concat('%', #{customerOrderNumber}, '%')

order by a.create_time desc

select * from t_deliver_detail
where third_id = #{thirdId}
limit 1
