mapper.insert:
insert into t_deliver_receive_record (id, dealer_id, dealer_name, 
  type, upload_time, status, 
  create_time, approval_user_id, approval_time
  )
values (#{id,jdbcType=INTEGER}, #{dealerId,jdbcType=INTEGER}, #{dealerName,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{uploadTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{approvalUserId,jdbcType=INTEGER}, #{approvalTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select *,
case when type = 1 then '出货'
when type = 2 then '收货' else '未知' end type_desc,
case when status = -1 then '待确认'
when status = 1 then '已确认'
when status = 2 then '已提交'
when status = 3 then '已驳回' else '未知' end status_desc,
DATE_FORMAT(upload_time,'%Y-%m-%d %H:%i:%s') upload_time_str,
DATE_FORMAT(approval_time,'%Y-%m-%d %H:%i:%s') approval_time_str
from t_deliver_receive_record
where 1 = 1
and status != 0

  and upload_time between #{uploadStartTime} and #{uploadEndTime}


  and upload_time >= #{uploadStartTime}


   and upload_time <= #{uploadEndTime} 


  and dealer_name like concat('%',#{dealerName},'%')


  and status = #{status}


  and dealer_id = #{dealerId}


  and type = #{type}

order by create_time desc

select t.status from t_deliver_receive_record t where 1 = 1
#{item}
group by t.status
