mapper.select:
select d.dealer_name,t.* from t_receive_detail t
inner join t_deliver_receive_record d on t.record_id = d.id
where record_id = #{recordId,jdbcType=INTEGER}

select b.* from t_deliver_receive_record a
inner join t_receive_detail b on a.id = b.record_id
where a.dealer_id = #{dealerId,jdbcType=INTEGER}

  and a.upload_time between #{uploadStartTime,jdbcType=VARCHAR} and #{uploadEndTime,jdbcType=VARCHAR}


  and a.upload_time >= #{uploadStartTime,jdbcType=VARCHAR}


   and a.upload_time <= #{uploadEndTime,jdbcType=VARCHAR} 



  and b.delivery_time between #{handoverStartTime,jdbcType=VARCHAR} and #{handoverEndTime,jdbcType=VARCHAR}


  and b.delivery_time >= #{handoverStartTime,jdbcType=VARCHAR}


   and b.delivery_time <= #{handoverEndTime,jdbcType=VARCHAR} 


  and b.product_model like concat('%', #{productModel,jdbcType=VARCHAR}, '%')


  and b.warehouse like concat('%', #{warehouse,jdbcType=VARCHAR}, '%')


  and b.delivery_company like concat('%', #{deliveryCompany,jdbcType=VARCHAR}, '%')

order by a.create_time desc

select * from t_receive_detail
where third_id = #{thirdId,jdbcType=VARCHAR}
limit 1
