mapper.select:
select * from t_deliver_order_approval
where 1=1

  and create_user_id = #{dealerId,jdbcType=INTEGER}


  and create_user_id = #{createUserId,jdbcType=INTEGER}


  and approval_status = #{approvalStatus,jdbcType=INTEGER}


  and approval_date BETWEEN #{approvalStartDate,jdbcType=VARCHAR} and #{approvalEndDate,jdbcType=VARCHAR}


  and approval_date <= #{approvalEndDate,jdbcType=VARCHAR}


  and approval_date > #{approvalStartDate,jdbcType=VARCHAR}


  and sap_order_no = #{salesOrderNo,jdbcType=VARCHAR}


  and create_time BETWEEN #{createStartDate,jdbcType=VARCHAR} and #{createEndDate,jdbcType=VARCHAR}


  and create_time <= #{createEndDate,jdbcType=VARCHAR}


  and create_time > #{createStartDate,jdbcType=VARCHAR}


  #{item,jdbcType=INTEGER}

select * from t_deliver_order_approval
where active =1 and approval_status = 0
and sap_order_no = #{salesOrderNo,jdbcType=VARCHAR}
