mapper.select:
select *  from t_deliver_order where active =1

  and create_user_id = #{dealerId,jdbcType=INTEGER}


  and create_user_id = #{createUserId,jdbcType=INTEGER}


  and sap_deliver_order_no = #{deliveryOrderNo,jdbcType=VARCHAR}


  and sap_order_no = #{salesOrderNo,jdbcType=VARCHAR}


  and actual_delivery_date BETWEEN #{actualDeliveryStartDate,jdbcType=VARCHAR} and #{actualDeliveryEndDate,jdbcType=VARCHAR}


  and actual_delivery_date <= #{actualDeliveryEndDate,jdbcType=VARCHAR}


  and actual_delivery_date > #{actualDeliveryStartDate,jdbcType=VARCHAR}



  and deliver_date BETWEEN #{deliveryStartDate,jdbcType=VARCHAR} and #{deliveryEndDate,jdbcType=VARCHAR}


  and deliver_date <= #{deliveryEndDate,jdbcType=VARCHAR}


  and deliver_date > #{deliveryStartDate,jdbcType=VARCHAR}
