mapper.select:
select *  from t_deliver_order where active =1

  and create_user_id = #{dealerId}


  and create_user_id = #{createUserId}


 and approval_status = #{approvalStatus}


  and approval_date BETWEEN approvalStartDate and approvalEndDate


  and approval_date <= approvalEndDate


  and approval_date > approvalStartDate


  and sap_deliver_order_no = #{deliveryOrderNo}


  and sap_order_no = #{salesOrderNo}


  and actual_delivery_date BETWEEN actualDeliveryStartDate and actualDeliveryEndDate


  and actual_delivery_date <= actualDeliveryEndDate


  and actual_delivery_date > actualDeliveryStartDate

select * from t_deliver_order where sap_deliver_order_no not in (
  select sap_delivery_id from t_order_invoice where active =1
  )
