mapper.select:
select

from t_order_apply
where 1=1

  and r_sap_order_id = #{rSapOrderId,jdbcType=VARCHAR}


  and dealer_id = #{dealerId,jdbcType=INTEGER}


  and create_time >= #{createBeginTime,jdbcType=TIMESTAMP}


  and create_time <= #{createEndTime,jdbcType=TIMESTAMP}


  and order_type = #{orderType,jdbcType=VARCHAR}


  and under_order_type = #{underOrderType,jdbcType=VARCHAR}


  and under_order_type = #{underOrderType,jdbcType=VARCHAR}


  and sold_to = #{soldTo,jdbcType=VARCHAR}


  and send_to = #{sendTo,jdbcType=VARCHAR}


  and purchase_no = #{purchaseNo,jdbcType=VARCHAR}


  and approval_status = #{approvalStatus,jdbcType=INTEGER}


  and active = #{active,jdbcType=INTEGER}

order by create_time desc
