mapper.select:
select

from t_catalog_price
where product_model = #{productModel,jdbcType=VARCHAR}
and bu = #{bu,jdbcType=VARCHAR}

select

from t_catalog_price
where product_model = #{productModel,jdbcType=VARCHAR}
and bu = #{bu,jdbcType=VARCHAR}
and in_customer = #{inCustomer,jdbcType=VARCHAR}

select

from t_catalog_price
where product_model = #{productModel,jdbcType=VARCHAR}
and platform = #{platform,jdbcType=VARCHAR}

select

from t_catalog_price
where 1=1

and (

  sap_code in (
    select distinct product_id from t_order_line where create_id = #{createId,jdbcType=INTEGER}
  )

  or sap_code in (
    select distinct vm_number from t_forecast where create_user_id = #{createId,jdbcType=INTEGER}
  )
)

and in_customer in (
  select cust_abbreviation from t_customer_info
  where id in (
    select cust_id from t_cust_corporate_relationship where corporate_type='Z004-1'
    and corporate_id = (select in_code from t_customer_info where id = #{dealerId,jdbcType=INTEGER})
  )
)

or sap_code in (
  select distinct sap_code from t_enquiry_price where proposer = #{proposer,jdbcType=VARCHAR}
)


  and bu = #{bu,jdbcType=VARCHAR}


  and pdt = #{pdt,jdbcType=VARCHAR}


  and status = #{status,jdbcType=VARCHAR}


  and platform = #{platform,jdbcType=VARCHAR}


  and product_model = #{productModel,jdbcType=VARCHAR}


  and price_type = #{priceType,jdbcType=VARCHAR}


  and in_customer = #{inCustomer,jdbcType=VARCHAR}


  and effect_time >= #{effectBeginTime,jdbcType=VARCHAR}


  and effect_time <= #{effectEndTime,jdbcType=VARCHAR}
