mapper.select:
select

from t_agency_rate
where active = 1

  and customer_type like concat('%',#{customerType},'%')


  and inside_customer like concat('%',#{insideCustomer},'%')


  and product_level_one = #{productLevelOne}


  and product_level_two = #{productLevelTwo}


  and product_level_three = #{productLevelThree}


  and product_type = #{productType}


  and basic_agency_rate = #{basicAgencyRate}


  and float_agency_rate = #{floatAgencyRate}


  and valid_start_date = #{validStartDate}


  and valid_start_date >= DATE_FORMAT(#{startTime},'%Y%m%d')


   and valid_start_date < DATE_FORMAT(#{endTime},'%Y%m%d') 


  and valid_start_date between DATE_FORMAT(#{startTime},'%Y%m%d') and DATE_FORMAT(#{endTime},'%Y%m%d')

order by create_time desc
