mapper.select:
select * from t_dealer_rate rate
inner join t_dealer_rate_batch batch on rate.batch_id = batch.id
where 1=1

  and rate.batch_id = #{batchId}


  and batch.batch_status = #{batchStatus}


  and cust_type = #{custType}


  and product_type_one = #{productTypeOne}


  and product_type_two=#{productTypeTwo}


  and product_type_three = #{productTypeThree}


  and product_type = #{productType}


  and valid_start >= #{startTime}


   and valid_start < #{endTime} 


  and valid_start between #{startTime} and #{endTime}
