mapper.insert:
insert into t_internal_user (in_user_id, user_no, user_name, 
  user_mail, user_mobile, user_department_code, 
  user_department_name, user_position_code, user_position_name, 
  insert_time, update_time, active
  )
values (#{inUserId,jdbcType=INTEGER}, #{userNo,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, 
  #{userMail,jdbcType=VARCHAR}, #{userMobile,jdbcType=VARCHAR}, #{userDepartmentCode,jdbcType=VARCHAR}, 
  #{userDepartmentName,jdbcType=VARCHAR}, #{userPositionCode,jdbcType=VARCHAR}, #{userPositionName,jdbcType=VARCHAR}, 
  #{insertTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{active,jdbcType=INTEGER}
  )

mapper.select:
select * from t_internal_user where user_department_code in (
  select seq from t_organizational_structure where parent_org in ('1001013','1001014')
  )
